mapper.select:
select count(1) as totoal
from gas_tank_${tenantcode}  
where
tenantcode=#{tenantcode}
and DATE_FORMAT(jdsj,'%Y-%m-%d')=DATE_FORMAT(now(),'%Y-%m-%d')

mapper.insert:
insert into gas_tank_${tenantcode}( tenantcode, officecode, gpbm, xpbm, jdsj, jdr, gpid, zdbm, scrq, sccj, ggxh, medium, gplx, pz, synx, qjzq, jdfs, ywzt, gpzt, last_check_time, next_check_time )values(
#{tenantcode} , #{officecode }, #{gpbm}, #{xpbm} , #{jdsj} , #{jdr} , #{gpid}, #{zdbm}, #{scrq}, #{sccj} , #{ggxh} , #{medium} , #{gplx} , #{pz} , #{synx} , #{qjzq} , #{jdfs} , #{ywzt} , #{gpzt} , #{lastCheckTime} , #{nextCheckTime}
)

mapper.select:

	select officecode ,gpbm,ggxh, DATEDIFF(now(),czsj) as lsts,gplx,scrq,jdsj,pz,fpr,zzcs,next_check_time as lastCheckTime,czsj,gpzt from gas_tank_${tenantcode} 
	 
	 	
		 
			and officecode = #{EQ_officecode}
		
		
		
			and gpbm like '%${LIKE_gpbm}%'
		
		
		
			and sccj = #{EQ_sccj}
		
		
		
			and zzcs  >= #{GT_zzcs}
		
		
		
			and DATEDIFF(now(),czsj) >= #{GT_lsts}
		
		
		
		
			and gpzt = #{EQ_gpzt}
		
		
		
		
		
			and scrq >= #{GTE_scrq}
		
		
		
			and scrq <= #{LTE_scrq}
		
		
		
			and czsj >= #{GTE_czsj}
		
		
		
			and czsj <= #{LTE_czsj}
		
		
		
			and jdsj >= #{GTE_jdsj}
		
		
		
			and jdsj <= #{LTE_jdsj}
		
		
		and del_flag = '0'
		order by jdsj desc
